import { useState } from "react";
import axios from "axios";

const TestForm = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [responseData, setResponseData] = useState([]);

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePostRequest = () => {
    const data = { name, email };

    axios
      .post("http://localhost:9000/testAPI", data)
      .then((response) => {
        console.log(response.data); // Optional: Log the response data
      })
      .catch((error) => {
        console.error("Post Error:", error);
      });
  };

  const handleGetRequest = () => {
    axios
      .get("http://localhost:9000/testAPI")
      .then((response) => {
        // if (response.statusCode === 20+0) {
          setResponseData(response.data);
        // } else {
        //   // setResponseData([{ name: "not", email: "found" }]);
        //   setResponseData("no data found");
        // }
        console.log("response: ", responseData);
      })
      .catch((error) => {
        console.error("GetError:", error);
      });
  };

  return (
    <div>
      <label>Name:</label>
      <input type="text" value={name} onChange={handleNameChange} />
      <br />
      <label>Email:</label>
      <input type="email" value={email} onChange={handleEmailChange} />
      <br />
      <button onClick={handlePostRequest}>Send POST Request</button>
      <br />
      <button onClick={handleGetRequest}>Send GET Request</button>
      <br />
      <p>
        Response Data: {responseData}
        {/* {responseData === ""
          ? "nothing"
          : responseData.map((user) => {
              return (
                <p>
                  name: {user.name}
                  <br />
                  email: {user.email}
                </p>
              );
            })} */}
      </p>
    </div>
  );
};

export default TestForm;
