import React from "react";
import { FcFolder, FcFile } from "react-icons/fc";
import "./files.css";

const FileStruct = ({ data }) => {
  const renderItems = (data) => {
    // if (!Array.isArray(items)) {
    //   return "nothing to show"; // or return [] if you prefer an empty array
    // }
    return items.map((item, index) => (
      <div className="ItemContainer" key={index}>
        {item.type === "folder" ? (
          <div className="FolderIconContainer">
            <FcFolder className="FolderIcon" />
            <span className="FolderName">{item.name}</span>
          </div>
        ) : (
          <div className="FileIconContainer">
            <FcFile className="FileIcon" />
            <span className="FileName">{item.name}</span>
          </div>
        )}
        {item.children && renderItems(item.children)}
      </div>
    ));
  };

  return <div className="FileComponent">{renderItems(data)}</div>;
};

export default FileStruct;
